mapper.select:
select id, article_id, user_id, content, parent_id, create_time
from user_comment
where article_id = #{id}
  and parent_id IS NULL
order by create_time desc
limit #{skip}, #{num}

select id, article_id, user_id, content, parent_id, create_time
from user_comment
where parent_id = #{id}
order by create_time desc
limit #{skip}, #{num}
