mapper.select:
select user_id, article_id, create_time
from user_like
where user_id=#{id}
order by create_time desc
limit #{skip}, #{num}

select user_id, article_id, create_time
from user_like
where article_id=#{id}
order by create_time desc
limit #{skip}, #{num}

select count(*)
from user_like
where user_id = #{id}

select count(*)
from user_like
where article_id = #{id}

select user_id, article_id, create_time
from user_like
where user_id = #{userId} and article_id=#{articleId}
